module.exports = function (polygon) {
  if ('geometry' in polygon) polygon = polygon.geometry
  if (!Array.isArray(polygon)) polygon = polygon.coordinates[0]

  var minx = miny = 1000
  ,   maxx = maxy = -1000
  for (var i = 0; i < polygon.length; i++) {
    var point = polygon[i]
    var x = point[0]
    var y = point[1]

    if (x < minx) minx = x
    else if (x > maxx) maxx = x
    if (y < miny) miny = y
    else if (y > maxy) maxy = y
  }

  return {
    type: 'Point',
    coordinates: [
      minx + ((maxx - minx) / 2),
      miny + ((maxy - miny) / 2)
    ]
  }
}
